graphql.query:
mutation signup($name: String!, $email: String!, $password: String!) {
  signup(name: $name, email: $email, password: $password) {
    __typename
    ... on SignupResultSuccess {
      user {
        C_User_ID
        C_User_UU
        created
        updated
        isActive
        name
        email
        isUserVerified
      }
    }
    ... on SignupResultError {
      message
    }
    ... on Error {
      __typename
      message
    }
  }
}

mutation generateUserVerification($email: String!, $password: String!) {
  verifyUser(email: $email, password: $password) {
    __typename
    ... on UserVerificationResultSuccess {
      user {
        C_User_ID
        C_User_UU
        created
        updated
        isActive
        name
        email
        isUserVerified
      }
    }
    ... on UserVerificationResultError {
      message
    }
    ... on Error {
      __typename
      message
    }
  }
}

mutation verifyUser(
  $email: String!
  $password: String!
  $verificationCode: String
) {
  verifyUser(
    email: $email
    password: $password
    verificationCode: $verificationCode
  ) {
    __typename
    ... on UserVerificationResultSuccess {
      user {
        C_User_ID
        C_User_UU
        created
        updated
        isActive
        name
        email
        isUserVerified
        userRoles {
          created
          isActive
          role
        }
      }
    }
    ... on UserVerificationResultError {
      message
    }
    ... on Error {
      __typename
      message
    }
  }
}

mutation login($email: String!, $password: String!, $fcmToken: String!) {
  login(email: $email, password: $password, fcmToken: $fcmToken) {
    __typename
    ... on LoginResultSuccess {
      token
      user {
        C_User_ID
        C_User_UU
        created
        updated
        isActive
        name
        email
        userRoles {
          C_UserRole_ID
          C_UserRole_UU
          created
          updated
          isActive
          roleName
        }
      }
    }
    ... on LoginResultError {
      message
    }
    ... on Error {
      __typename
      message
    }
  }
}

query me {
  me {
    __typename
    ... on User {
      C_User_ID
      C_User_UU
      created
      updated
      isActive
      name
      email
      userRoles {
        C_UserRole_ID
        C_UserRole_UU
        created
        updated
        isActive
        roleName
      }
      userPaymentMethods {
          name
      }
    }
    ... on NotAuthenticatedError {
      message
    }
    ... on Error {
      __typename
      message
    }
  }
}

mutation updateUser($C_User_ID: ID!, $input: UserInput!) {
  updateUser(
      C_User_ID: $C_User_ID,
      input: $input
  ) {
      __typename
    ... on User {
      C_User_ID
    }
    ... on Error {
      __typename
      message
    }
  }
}

