graphql.query:
mutation signUp($name: String!, $email: String!, $password: String!) {
  signUp(name: $name, email: $email, password: $password) {
    __typename
    ... on SignUpResultSuccess {
      user {
        C_User_ID
        C_User_UU
        created
        updated
        isActive
        name
        email
        isUserVerified
      }
    }
    ... on SignUpResultError {
      message
    }
    ... on Error {
      message
    }
  }
}

mutation generateUserVerification($email: String!, $password: String!) {
  verifyUser(email: $email, password: $password) {
    __typename
    ... on UserVerificationResultSuccess {
      user {
        C_User_ID
        C_User_UU
        created
        updated
        isActive
        name
        email
        isUserVerified
      }
    }
    ... on UserVerificationResultError {
      message
    }
    ... on Error {
      message
    }
  }
}

mutation verifyUser(
  $email: String!
  $password: String!
  $verificationCode: String
) {
  verifyUser(
    email: $email
    password: $password
    verificationCode: $verificationCode
  ) {
    __typename
    ... on UserVerificationResultSuccess {
      user {
        C_User_ID
        C_User_UU
        created
        updated
        isActive
        name
        email
        isUserVerified
        userRoles {
          created
          isActive
          role
        }
      }
    }
    ... on UserVerificationResultError {
      message
    }
    ... on Error {
      message
    }
  }
}

mutation login($email: String!, $password: String!, $fcmToken: String!) {
  login(email: $email, password: $password, fcmToken: $fcmToken) {
    __typename
    ... on LoginResultSuccess {
      token
      user {
        C_User_ID
        C_User_UU
        created
        updated
        isActive
        name
        email
        userRoles {
          C_UserRole_ID
          C_UserRole_UU
          created
          updated
          isActive
          roleName
        }
      }
    }
    ... on LoginResultError {
      message
    }
    ... on Error {
      message
    }
  }
}

query me {
  me {
    __typename
    ... on User {
      C_User_ID
      C_User_UU
      created
      updated
      isActive
      name
      email
      userRoles {
        C_UserRole_ID
        C_UserRole_UU
        created
        updated
        isActive
        roleName
      }
      userWallets {
          name
      }
    }
    ... on NotAuthenticatedError {
      message
    }
    ... on Error {
      message
    }
  }
}

mutation renewMyCurrentQRCode {
  renewMyCurrentQRCode {
    __typename
    ... on UserQRCode {
      schemaVersion
      dataType
      data
    }
    ... on UserQRCodeError {
      message
    }
    ... on Error {
      message
    }
  }
}

query myCurrentQRCode {
  myCurrentQRCode {
    __typename
    ... on UserQRCode {
      schemaVersion
      dataType
      data
    }
    ... on UserQRCodeError {
      message
    }
    ... on Error {
      message
    }
  }
}

query userWallet($C_UserWallet_ID: ID!) {
  userWallet(C_UserWallet_ID: $C_UserWallet_ID) {
    __typename
    ... on UserWallet {
      name
    }
    ... on UserWalletError {
      message
    }
    ... on Error {
      message
    }
  }
}

query userWallets($C_User_ID: ID!) {
  userWallets(C_User_ID: $C_User_ID) {
    __typename
    ... on UserWallets {
      userWallets {
          user {
              name
          }
          billingLocation {
              line1
          }
          name
      }
    }
    ... on UserWalletError {
      message
    }
    ... on Error {
      message
    }
  }
}
